osition(Vec2(visibleSize.width*7.5/10,visibleSize.height/2.0));//    addChild(hero);        auto backCastle = Button::create("seasonwin/continue.png");    backCastle->setPosition(Vec2(visibleSize.width*5.0/10.0,visibleSize.height*2.0/10.0));    backCastle->addTouchEventListener([&](Ref*pSender,Widget::TouchEventType type){        if(type==Widget::TouchEventType::ENDED){            if(count>=72){                bool  isPlayMusic = UserDefault::getInstance()->getBoolForKey("music", true);                if(isPlayMusic){                    CocosDenshion::SimpleAudioEngine::getInstance()->stopBackgroundMusic();                    CocosDenshion::SimpleAudioEngine::getInstance()->preloadBackgroundMusic("xuqu.mp3");                    CocosDenshion::SimpleAudioEngine::getInstance()->playBackgroundMusic("xuqu.mp3", true);                }                  Director::getInstance()->replaceScene(Tail::createScene());                  Director::getInstance()->resume();            }else{                bool  isPlayMusic = UserDefault::getInstance()->getBoolForKey("music", true);                if(isPlayMusic){                    CocosDenshion::SimpleAudioEngine::getInstance()->stopBackgroundMusic();                    CocosDenshion::SimpleAudioEngine::getInstance()->preloadBackgroundMusic("xuqu.mp3");                    CocosDenshion::SimpleAudioEngine::getInstance()->playBackgroundMusic("xuqu.mp3", true);                }                  Director::getInstance()->resume();                Director::getInstance()->replaceScene(CastleScene::createScene());            }        }    });    addChild(backCastle);    int beanNumber = UserDefault::getInstance()->getIntegerForKey("bean",1);    if(beanNumber+50<9999){        beanNumber+=50;    }else{        beanNumber = 9999;    }    UserDefault::getInstance()->setIntegerForKey(StringUtils::format("true_max_level%d",currentSeason).c_str(), 13);    UserDefault::getInstance()->setIntegerForKey("bean", beanNumber);    UserDefault::getInstance()->flush();    return true;}